css:
/*********************** Tab02 ****************************/

.designportal h1,
.designportal h2,
.designportal h3,
.designportal h4,
.designportal p,
.designportal td,
.designportal th,
.designportal ul,
.designportal dl,
.designportal dd,
.designportal dt,
.designportal ol,
.designportal button {
    margin: 0;
    padding: 0;
}

.designportal ul {
    list-style: disc;
}

.designportal ol {
    list-style: decimal;
}

.designportal button {
    border: none;
}

.designportal-advanced {
    display: block;
    position: relative;
    padding: 20px 0 0 0;
    background: #d8d8d8;
}

.designportal-advanced .advanced-list-item {
    font-size: 1rem;
    margin-bottom: 5px;
}

@media screen and (max-width: 1000px) {
    .designportal-advanced .advanced-list-item {
        font-size: 1.6rem;
        margin-bottom: 10px;
    }
}

.designportal-advanced .advanced-panel--active {
    background-color: #ffffff;
    color: #333333;
    z-index: 2;
}

.designportal-advanced .advanced-container {
    margin: 0 auto;
    max-width: 1800px;
    width: 100%;
}

.designportal-advanced .advanced-tab-container {
    display: flex;
    list-style-type: none;
    text-align: center;
    margin: 0 10px 0 10px;
    color: #333333;
}

@media screen and (max-width: 576px) {
    .designportal-advanced .advanced-tab-container {
        margin: 0 10px;
    }
}

.designportal-advanced .advanced-tab {
    display: block;
    flex: 1;
    width: 100%;
    margin-right: 20px;
    align-content: stretch;
    padding: 15px 10px;
    position: relative;
    font-size: 1.2rem;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    background-color: #151719;
    color: #ffffff;
    cursor: pointer;
}

.designportal-advanced .advanced-tab:last-child {
    margin-right: 0;
}

.designportal-advanced .advanced-tab:hover {
    background-color: #666666;
    color: #ffffff;
}

.designportal-advanced .advanced-tab:focus {
    background-color: #f2f2f2;
    color: #333333;
}

@media screen and (max-width: 1000px) {
    .designportal-advanced .advanced-tab {
        font-size: 1.2rem;
    }
}

@media screen and (max-width: 576px) {
    .designportal-advanced .advanced-tab {
        margin-right: 10px;
    }
}

.designportal-advanced .advanced-tab--active {
    background-color: #f2f2f2;
    color: #333333;
}

.designportal-advanced .advanced-panel-container {
    display: block;
    position: relative;
    //margin: 0 10px 10px;
    background-color: #ffffff;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}

.designportal-advanced .advanced-list-container {
    display: flex;
}

@media screen and (max-width: 576px) {
    .designportal-advanced .advanced-list-container {
        display: block;
    }
}

.designportal-advanced .advanced-list {
    flex: 1;
    padding: 0 0 18px 30px;
    margin: 0 15px;
}

.designportal-advanced .advanced-list:first-child {
    margin-left: 0;
}

.designportal-advanced .advanced-list:last-child {
    margin-right: 0;
}

@media screen and (max-width: 576px) {
    .designportal-advanced .advanced-list {
        margin: 0;
    }
}

.designportal-advanced .advanced-panel-contents {
    display: none;
    padding: 10px 0 0 0;
    clear: both;
    overflow: hidden;
    color: #333333;
}

@media screen and (max-width: 1000px) {
    .designportal-advanced .advanced-panel-contents {
        padding: 10px 0 0 0;
    }
}

.designportal-advanced .advanced-panel-contents--active {
    overflow: hidden;
    display: block;
}

.designportal-advanced .advanced-panel-contents-group {
    display: flex;
}

@media screen and (max-width: 1000px) {
    .designportal-advanced .advanced-panel-contents-group {
        flex-direction: column;
    }
}

.designportal-advanced .advanced-data {
    display: block;
}

.designportal-advanced .advanced-img {
    width: 100%;
}

.designportal-advanced .advanced-data--graph {
    width: 100%;
}

.designportal-advanced .advanced-heading1 {
    font-size: 2rem;
    font-weight: bold;
    margin-bottom: 20px;
}

@media screen and (max-width: 1000px) {
    .designportal-advanced .advanced-heading1 {
        font-size: 2.6rem;
    }
}

.designportal-advanced .advanced-heading2 {
    font-size: 1.5rem;
    font-weight: bold;
    margin-bottom: 20px;
}

@media screen and (max-width: 1000px) {
    .designportal-advanced .advanced-heading2 {
        font-size: 2rem;
    }
}

.designportal-advanced .advanced-text {
    font-size: 1rem;
    margin-bottom: 20px;
}

@media screen and (max-width: 1000px) {
    .designportal-advanced .advanced-text {
        font-size: 1.2rem;
    }
}

.designportal-advanced .advanced-panel-contents-unit {
    flex: 1;
    margin: 0 30px 30px 0;
}

.designportal-advanced .advanced-panel-contents-unit:last-child {
    margin-right: 0;
}


/* Standard portal weget */

.gaia-argoui-widget {
    border: 1px solid #eee;
}


/* Map image */

.mt-contents {
    background-size: cover;
    background-color: #f2f2f2;
}


/*********************** Tab05 ****************************/
/* Grid css */

.gird-container {
    margin: 0 auto;
    padding: 0px;
    width: 100%;
    columns: auto 17em;
    column-gap: 0;
}

.grid-item {
    margin: 0 5px 5px 10px;
    width: 85%;
    height: 100%;
    padding: 8px;
    background-color: #fff;
    border: 1px solid #eee;
    opacity: 0.8;
}

.grid-item img {
    width: 100%;
}

.cardsportal {
  padding: 20px 0 0 0;
}

/*iframe css*/

.map-container {
    margin: 0 10px 5px 10px;
}

.map-iframe {
    width: 100%;
    height: 500px;
    -webkit-filter: hue-rotate(100deg);
    filter: hue-rotate(100deg);
    display: block;
    pointer-events: auto;
    /* or none auto */
    position: relative;
    /* IE needs a position other than static */
}


/*********************** Tab03 ****************************/

.designportal-basic {
    display: block;
    position: relative;
    background-color: #F2F2F2;
}

@media screen and (max-width: 1160px) {
    .designportal-basic {
        background-image: none;
    }
}

@media screen and (max-width: 768px) {
    .designportal-basic {
        background-image: none;
    }
}

.designportal-basic h1,
.designportal-basic h2,
.designportal-basic h3,
.designportal-basic ul,
.designportal-basic ol,
.designportal-basic p,
.designportal-basic th,
.designportal-basic td,
.designportal-basic dl,
.designportal-basic dt,
.designportal-basic dd {
    margin: 0;
    padding: 0;
}

.designportal-basic ul {
    list-style: disc;
}

.designportal-basic ol {
    list-style: decimal;
}

.designportal .basic-container {
    width: 960px;
    margin: 0 auto;
    padding: 40px 0 0 0;
}

.designportal .basic-container:last-child {
    padding-bottom: 40px;
}

@media screen and (max-width: 1160px) {
    .designportal .basic-container {
        width: 80vw;
        text-align: left;
        margin: 0;
        padding: 30px 10vw 5px 10vw;
    }
}

@media screen and (max-width: 768px) {
    .designportal .basic-container {
        width: 80vw;
        text-align: left;
        margin: 0;
        padding: 30px 10vw 5px 10vw;
    }
}

.designportal .basic-spaceSet {
    display: flex;
    flex-wrap: wrap;
    background-color: rgba(255, 255, 255, 1);
    box-shadow: 0px 5px 10px rgba(0, 0, 0, 0.05);
    border-radius: 10px;
    justify-content: left;
    list-style-type: none;
}

@media screen and (max-width: 768px) {
    .designportal .basic-spaceSet {
        display: block;
        box-shadow: none;
        border-radius: 0;
        padding: 0;
    }
}

.designportal .basic-space {
    display: block;
    width: 220px;
    margin: 30px;
}

@media screen and (max-width: 1160px) {
    .designportal .basic-space {
        display: block;
        width: 30%;
        margin: 20px 10px 10px 10px;
    }
}

@media screen and (max-width: 768px) {
    .designportal .basic-space {
        width: auto;
        margin: 0 auto;
        padding: 0 0 10px 0;
        border-bottom: solid 1px #dddddd;
    }
    .designportal .basic-space:last-child {
        border-bottom: none;
    }
}

.designportal .basic-space-link,
.designportal .basic-app-link {
    display: block;
    color: #000000;
    text-decoration: none;
}

@media screen and (max-width: 768px) {
    .designportal .basic-space-link,
    .designportal .basic-app-link {
        display: flex;
        align-items: center;
    }
}

.designportal .basic-space-link:hover,
.designportal .basic-app-link:hover,
.designportal .basic-space-link:focus,
.designportal .basic-app-link:focus {
    text-decoration: underline;
}

.designportal .basic-space-link:focus,
.designportal .basic-app-link:focus {
    outline: #3498db solid 1px;
}

.designportal .basic-space-img-container {
    height: 100px;
    border-radius: 10px;
}

@media screen and (max-width: 1160px) {
    .designportal .basic-space-img-container {
        width: 100%;
        height: 100px;
    }
}

@media screen and (max-width: 768px) {
    .designportal .basic-space-img-container {
        width: 40%;
        height: 50px;
        border-radius: 12px;
    }
}

.designportal .basic-space-img {
    display: block;
    height: 100px;
    border-radius: 10px;
    background-repeat: no-repeat;
    background-position: left;
    background-size: 280% auto;
}

@media screen and (max-width: 768px) {
    .designportal .basic-space-img {
        width: 80vw;
        height: 60px;
        border-radius: 0;
        background-position: center;
        background-size: 100% auto;
    }
}

.designportal .basic-space-name {
    font-size: 1.2rem;
    margin-top: 10px;
    text-align: center;
    font-weight: bold;
}

@media screen and (max-width: 768px) {
    .designportal .basic-space-name {
        padding-top: 10px;
    }
}

.designportal .basic-space-introduction {
    width: 66%;
    margin: 30px 0 0 30px;
    font-size: 1rem;
}

@media screen and (max-width: 1160px) {
    .designportal .basic-space-introduction {
        width: 60%;
        margin: 2.5% 0 0 30px;
        padding-bottom: 20px;
        font-size: 1rem;
    }
}

@media screen and (max-width: 768px) {
    .designportal .basic-space-introduction {
        width: auto;
        margin: 20px 0 0 30px;
        padding-bottom: 20px;
        font-size: 1rem;
    }
}

.designportal .basic-appSet {
    display: none;
    flex-wrap: wrap;
    margin: 0 auto;
}

@media screen and (max-width: 1160px) {
    .designportal .basic-appSet {
        min-width: 75vw;
    }
}

@media screen and (max-width: 768px) {
    .designportal .basic-appSet {
        padding-left: 10px;
    }
}

.designportal .basic-app {
    display: block;
    width: 120px;
    margin: 20px;
    list-style-type: none;
}

@media screen and (max-width: 1160px) {
    .designportal .basic-app {
        width: 14%;
        margin: 20px 7% 20px 0;
    }
    .designportal .basic-app:first-child {
        margin-left: 0;
    }
    .designportal .basic-app:last-child {
        margin-right: 0;
    }
}

@media screen and (max-width: 768px) {
    .designportal .basic-app {
        width: auto;
        margin: 0;
        padding: 10px 15px;
    }
    .designportal .basic-app:last-child {
        border-bottom: none;
    }
}

.designportal .app-widthsmall {
    width: 85px;
}

@media screen and (max-width: 1160px) {
    .designportal .app-widthsmall {
        width: 70px;
        height: 100px;
    }
}

.designportal .basic-app-icon {
    width: 100px;
    height: 100px;
    margin: 0 auto;
    border-radius: 25px;
    text-align: center;
}

@media screen and (max-width: 768px) {
    .designportal .basic-app-icon {
        width: 80px;
        height: 100px;
    }
}

.designportal .basic-app-icon-img {
    display: block;
    width: 70px;
    height: 70px;
    padding: 15px;
    fill: #FFFFFF;
}

.designportal .basic-app-name {
    font-size: 1rem;
    margin-top: 10px;
}

@media screen and (max-width: 1160px) {
    .designportal .basic-app-name {
        font-size: .9rem;
    }
}


/*********************** Tab04 ****************************/

@media screen and (max-width:864px) {
    .poster-background[data-v-eaa6e270] {
        padding-top: 60px
    }
    .poster-background .base-button[data-v-eaa6e270] {
        margin-top: 30px
    }
}

.poster-background[data-v-eaa6e270] {
    position: relative;
    height: 100vh
}

.poster-background.no-posters[data-v-eaa6e270] {
    height: 0
}

.posters[data-v-eaa6e270] {
    height: 100%
}

.poster[data-v-eaa6e270] {
    transform: none;
    padding: 20px
}

@media screen and (min-width:1441px) {
    .poster[data-v-eaa6e270] {
        padding: 1em
    }
}

.poster-wrap[data-v-eaa6e270] {
    padding: 0
}

.poster-background .posters .lax-wrap[data-v-eaa6e270] {
    position: absolute;
    padding: 0;
    pointer-events: none
}

.poster-background .posters .lax-wrap .poster-wrap[data-v-eaa6e270] {
    pointer-events: auto;
    transition: transform 1333ms cubic-bezier(.33, 0, .04, 1)
}

.poster-background .posters .lax-wrap[data-v-eaa6e270]:nth-child(1) {
    bottom: 0;
    right: 5%
}

@media screen and (min-width:865px) {
    .poster-background[data-v-eaa6e270] {
        height: 100%;
        left: 0;
        position: absolute;
        top: 0;
        width: 100%
    }
    .poster[data-v-eaa6e270] {
        transform: none
    }
    .hovering .poster[data-v-eaa6e270] img {
        opacity: .5
    }
    .active .poster[data-v-eaa6e270] img {
        opacity: 1
    }
    .poster-background .posters .lax-wrap.active[data-v-eaa6e270] {
        z-index: 50
    }
    .poster-background .posters .lax-wrap:nth-child(1) .poster-wrap[data-v-eaa6e270],
    .poster-background .posters .lax-wrap:nth-child(2) .poster-wrap[data-v-eaa6e270] {
        transform: translate(0, 0) rotate(-10deg)
    }
    .poster-background .posters .lax-wrap:nth-child(1) .poster-wrap .poster[data-v-eaa6e270],
    .poster-background .posters .lax-wrap:nth-child(2) .poster-wrap .poster[data-v-eaa6e270] {
        transition: 67ms cubic-bezier(.33, 0, 0, 1)
    }
    .poster-background .posters .lax-wrap:nth-child(1) .poster-wrap:hover .poster[data-v-eaa6e270],
    .poster-background .posters .lax-wrap:nth-child(2) .poster-wrap:hover .poster[data-v-eaa6e270] {
        transform: rotate(-20deg)
    }
}

.slice:not(.inview) .poster-background .posters .lax-wrap:nth-child(1) .poster-wrap[data-v-eaa6e270] {
    transform: translate(120%, 0) rotate(-30deg)
}

.poster-background .posters .lax-wrap[data-v-eaa6e270]:nth-child(2) {
    top: 40%;
    left: 0
}

.slice:not(.inview) .poster-background .posters .lax-wrap:nth-child(2) .poster-wrap[data-v-eaa6e270] {
    transform: translate(-180%, 0) rotate(30deg)
}

.poster-background .posters .lax-wrap[data-v-eaa6e270]:nth-child(3) {
    left: 75%;
    top: 0
}

.poster-background .posters .lax-wrap:nth-child(3) .poster-wrap[data-v-eaa6e270],
.poster-background .posters .lax-wrap:nth-child(4) .poster-wrap[data-v-eaa6e270] {
    transition-delay: 167ms
}

@media screen and (min-width:865px) {
    .poster-background .posters .lax-wrap:nth-child(3) .poster-wrap[data-v-eaa6e270] {
        transform: translate(0, -40%) rotate(-10deg)
    }
    .poster-background .posters .lax-wrap:nth-child(3) .poster-wrap .poster[data-v-eaa6e270],
    .poster-background .posters .lax-wrap:nth-child(4) .poster-wrap .poster[data-v-eaa6e270] {
        transition: 67ms cubic-bezier(.33, 0, 0, 1)
    }
    .poster-background .posters .lax-wrap:nth-child(3) .poster-wrap:hover .poster[data-v-eaa6e270],
    .poster-background .posters .lax-wrap:nth-child(4) .poster-wrap:hover .poster[data-v-eaa6e270] {
        transform: rotate(20deg)
    }
    .poster-background .posters .lax-wrap:nth-child(3) .poster-wrap[data-v-eaa6e270] .ui {
        top: 100%;
        bottom: auto
    }
    .poster-background .posters .lax-wrap:nth-child(3) .poster-wrap[data-v-eaa6e270] .ui svg {
        top: 0
    }
    .poster-background .posters .lax-wrap:nth-child(4) .poster-wrap[data-v-eaa6e270] {
        transform: translate(0, 40%) rotate(-10deg)
    }
}

.slice:not(.inview) .poster-background .posters .lax-wrap:nth-child(3) .poster-wrap[data-v-eaa6e270] {
    transform: translate(0, -115%) rotate(-30deg)
}

.poster-background .posters .lax-wrap[data-v-eaa6e270]:nth-child(4) {
    bottom: 12%;
    left: 24%
}

.slice:not(.inview) .poster-background .posters .lax-wrap:nth-child(4) .poster-wrap[data-v-eaa6e270] {
    transform: translate(0, 105%) rotate(-30deg)
}

.poster-background .posters .lax-wrap[data-v-eaa6e270]:nth-child(5) {
    z-index: 4;
    top: 0;
    left: 10%
}

.poster-background .posters .lax-wrap:nth-child(5) .poster-wrap[data-v-eaa6e270],
.poster-background .posters .lax-wrap:nth-child(6) .poster-wrap[data-v-eaa6e270] {
    transition-delay: 333ms
}

@media screen and (min-width:865px) {
    .poster-background .posters .lax-wrap:nth-child(5) .poster-wrap[data-v-eaa6e270] {
        transform: translate(0, -50%) rotate(10deg)
    }
    .poster-background .posters .lax-wrap:nth-child(5) .poster-wrap .poster[data-v-eaa6e270],
    .poster-background .posters .lax-wrap:nth-child(6) .poster-wrap .poster[data-v-eaa6e270] {
        transition: 67ms cubic-bezier(.33, 0, 0, 1)
    }
    .poster-background .posters .lax-wrap:nth-child(5) .poster-wrap:hover .poster[data-v-eaa6e270] {
        transform: rotate(-20deg)
    }
    .poster-background .posters .lax-wrap:nth-child(5) .poster-wrap[data-v-eaa6e270] .ui {
        top: 100%;
        bottom: auto
    }
    .poster-background .posters .lax-wrap:nth-child(5) .poster-wrap[data-v-eaa6e270] .ui svg {
        top: 0
    }
    .poster-background .posters .lax-wrap:nth-child(6) .poster-wrap[data-v-eaa6e270] {
        transform: translate(0, 50%) rotate(10deg)
    }
    .poster-background .posters .lax-wrap:nth-child(6) .poster-wrap:hover .poster[data-v-eaa6e270] {
        transform: rotate(20deg)
    }
}

.slice:not(.inview) .poster-background .posters .lax-wrap:nth-child(5) .poster-wrap[data-v-eaa6e270] {
    transform: translate(0, -115%) rotate(30deg)
}

.poster-background .posters .lax-wrap[data-v-eaa6e270]:nth-child(6) {
    bottom: 13%;
    right: 30%;
}

.slice:not(.inview) .poster-background .posters .lax-wrap:nth-child(6) .poster-wrap[data-v-eaa6e270] {
    transform: translate(0, 110%) rotate(-30deg)
}

.poster-background .posters .lax-wrap[data-v-eaa6e270]:nth-child(7) {
    z-index: 4;
    top: 10%;
    left: 0
}

.poster-background .posters .lax-wrap:nth-child(7) .poster-wrap[data-v-eaa6e270] {
    transform: rotate(-10deg) translate(-40%, 0);
    transition-delay: 334ms
}

.slice:not(.inview) .poster-background .posters .lax-wrap:nth-child(7) .poster-wrap[data-v-eaa6e270] {
    transform: translate(-120%, 0) rotate(-30deg)
}

.poster-background .posters .lax-wrap[data-v-eaa6e270]:nth-child(8) {
    top: 10%;
    right: 6%;
}

.poster-background .posters .lax-wrap:nth-child(8) .poster-wrap[data-v-eaa6e270],
.poster-background .posters .lax-wrap:nth-child(9) .poster-wrap[data-v-eaa6e270] {
    transition-delay: .5s
}

@media screen and (min-width:865px) {
    .poster-background .posters .lax-wrap:nth-child(7) .poster-wrap[data-v-eaa6e270] {
        transform: translate(-40%, 0) rotate(-10deg)
    }
    .poster-background .posters .lax-wrap:nth-child(7) .poster-wrap .poster[data-v-eaa6e270],
    .poster-background .posters .lax-wrap:nth-child(8) .poster-wrap .poster[data-v-eaa6e270],
    .poster-background .posters .lax-wrap:nth-child(9) .poster-wrap .poster[data-v-eaa6e270] {
        transition: 67ms cubic-bezier(.33, 0, 0, 1)
    }
    .poster-background .posters .lax-wrap:nth-child(7) .poster-wrap:hover .poster[data-v-eaa6e270] {
